import 'package:ecommerceapp/consts/consts.dart';import 'package:ecommerceapp/consts/lists.dart';import 'package:ecommerceapp/widget_common/applogo_widget.dart';import 'package:ecommerceapp/widget_common/bg_widget.dart';import 'package:ecommerceapp/widget_common/custon_textfield.dart';import 'package:ecommerceapp/widget_common/our_button.dart';import 'package:flutter/material.dart';class LoginScreen extends StatelessWidget {  const LoginScreen({super.key});  @override  Widget build(BuildContext context) {    return bgWidget(        child: Scaffold(          resizeToAvoidBottomInset: false,      body: Center(        child: Column(          children: [            (context.screenHeight * 0.1).heightBox,            appLogoWidget(),            10.heightBox,            "Login in to $appname"                .text                .fontFamily(bold)                .size(18)                .white                .make(),            15.heightBox,            Column(              children: [                customTextField(hint: emailHint, title: email),                customTextField(hint: passwordHint, title: password),                Align(                  alignment: Alignment.centerRight,                  child: TextButton(                      onPressed: () {}, child: forgetPass.text.make()),                ),                5.heightBox,                // ourButton().box.width(context.screenWidth - 50).make(),                ourButton(                        color: redColor,                        title: login,                        textColor: whiteColor,                        onPressed: () {})                    .box                    .width(context.screenWidth - 50)                    .make(),                5.heightBox,                createNewAccount.text.color(fontGrey).make(),                5.heightBox,                ourButton(                        color: lightGolden,                        title: signup,      