import 'package:ecommerceapp/consts/consts.dart';import 'package:ecommerceapp/widget_common/applogo_widget.dart';import 'package:ecommerceapp/widget_common/bg_widget.dart';import 'package:ecommerceapp/widget_common/custon_textfield.dart';import 'package:ecommerceapp/widget_common/our_button.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class SignupScreen extends StatefulWidget {  const SignupScreen({Key? key}) : super(key: key);  @override  State<SignupScreen> createState() => _SignupScreenState();}class _SignupScreenState extends State<SignupScreen> {  bool? isCheck = false;  @override  Widget build(BuildContext context) {    return bgWidget(        child: Scaffold(      resizeToAvoidBottomInset: false,      body: Center(        child: Column(          children: [            (context.screenHeight * 0.1).heightBox,            appLogoWidget(),            10.heightBox,            "Join the $appName".text.fontFamily(bold).size(18).white.make(),            15.heightBox,            Column(              children: [                customTextField(hint: nameHint, title: name),                customTextField(hint: emailHint, title: email),                customTextField(hint: passwordHint, title: password),                customTextField(hint: passwordHint, title: retypePassword),                Align(                  alignment: Alignment.centerRight,                  child: TextButton(                      onPressed: () {}, child: forgetPass.text.make()),                ),                Row(children: [                  Checkbox(                      checkColor: whiteColor,                      activeColor: redColor,                      value: isCheck,                      onChanged: (newValue) {                        setState(() {                          isCheck = newValue;                        });                      }),                  5.widthBox,                  Expanded(                    child: RichText(                        text: const TextSpan(children: [                      TextSpan(                          text: "I agree to the ",                          style:                              TextStyle(color: fontGrey, fontFamily: regular)),                      TextSpan(                          text: termsAndConditions,                          style:                              TextStyle(color: redColor, fontFamily: regular)),                      TextSpan(                          text: ' &',                          style:                              TextStyle(color: redColor, fontFamily: regular)),                      TextSpan(                          text: privacyPolicy,                          style:                              TextStyle(color: redColor, fontFamily: regular)),                    ])),                  )                ]),                5.heightBox,                // ourButton().box.width(context.screenWidth - 50).make(),                ourButton(                        color: isCheck == true ? redColor : lightGrey,                        title: signup,                        textColor: whiteColor,                        onPressed: () {})                    .box                    .width(context.screenWidth - 50)                    .make(),                10.heightBox,                RichText(                  text: const TextSpan(                    children: [                      TextSpan(                          text: alreadyHaveAccount,                          style:                              TextStyle(color: fontGrey, fontFamily: regular)),                      TextSpan(                          text: login,                          style: TextStyle(color: redColor, fontFamily: bold)),                    ],                  ),                ).onTap(() {                  Get.back();                })              ],            )                .box                .white                .rounded                .padding(const EdgeInsets.all(16))                .width(context.screenWidth - 70)                .shadowSm                .make(),          ],        ),      ),    ));  }}