import 'package:ecommerceapp/consts/consts.dart';import 'package:ecommerceapp/consts/lists.dart';import 'package:ecommerceapp/views/category_screen/category_details.dart';import 'package:ecommerceapp/widget_common/bg_widget.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class CategoryScreen extends StatelessWidget {  const CategoryScreen({super.key});  @override  Widget build(BuildContext context) {    return bgWidget(      child: Scaffold(        appBar: AppBar(          title: categories.text.fontFamily(bold).white.make(),        ),        body: Container(          padding: const EdgeInsets.all(12),          child: GridView.builder(            shrinkWrap: true,            itemCount: 9,            gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(              crossAxisCount: 3,              mainAxisSpacing: 8,              crossAxisSpacing: 8,              mainAxisExtent: 200,            ),            itemBuilder: (BuildContext context, int index) {              return Column(                children: [                  Image.asset(                    categoriesImageList[index],                    width: 200,                    height: 120,                    fit: BoxFit.cover,                  ),                  10.heightBox,                  categoriesList[index]                      .text                      .color(darkFontGrey)                      .align(TextAlign.center)                      .make(),                ],              )                  .box                  .white                  .rounded                  .clip(Clip.antiAlias)                  .outerShadowSm                  .make().onTap(() {                Get.to(() => CategoryDetails(                  title: categoriesList[index],                ));              }); // Bạn cần trả về phần tử tại vị trí index ở đây.            },          ),        ),      ),    );  }}